"use strict";App.view.extend("reader",function(){this.container=function(){return"\n            <h1 class=\"fika-title\">\n                {{ data['title'] }}\n            </h1>\n            <div class=\"fika-content\">\n                {{ data['content'] }}\n            </div>\n        "},this.toc=function(){return"\n            {{ for var i in data }}\n            <a href=\"#{{ data[i]['id'] }}\"><{{ data[i]['tag'] }}>{{ data[i]['text'] }}</{{ data[i]['tag'] }}></a>\n            {{ end }}\n        "},this.fonts=function(){return"\n            {{ for var i in data }}\n                <div class=\"fika-select-font {{ 'font-' + data[i]['class'] }}\">{{ data[i]['name'] }}</div>\n            {{ end }}\n        "}});